import React from 'react'
import Nav from './Nav';
import { useState } from 'react';

const Main = ({token}) => {

  const [loggedIn, setLoggedIn] = useState(false)

  if (token) {
    window.location.href = '/'
    setLoggedIn(true);
  }
  return (
    <>
    <Nav /> 
    <div className='main'>   
      {loggedIn ? (
        <h1 className='front-text'>User Authorization with Node.js and React</h1>
      ) : (
        <h1 className='front-text'>User Authorization with Node.js and React</h1>
      )}
    </div>
      </>
  )
}

export default Main
