import { NavLink } from "react-router-dom"
import { useState, useEffect } from "react"

const Nav = () => {

    const [loggedIn, setLoggedIn] = useState(false)
    const [token, setToken] = useState("")

    // get token from localhost 3001 no localstorage
    useEffect(() => {
        const token = async () => {
            const res = await fetch("http://localhost:3001/token", {
                method: "GET",
                credentials: "include",
            })
            const data = await res.json()
            setToken(true)
        }
        token()
    }, [])

    // set loggedin to true if token is true
    useEffect(() => {
        if (token) {
            setLoggedIn(true)
        }
    }, [token])


    // logout 
    const logout = async () => {
        const res = await fetch("http://localhost:3001/token", {
            method: "DELETE",
            credentials: "include",
        })
        if(res.ok) {
            setLoggedIn(false)
        }
    }

return (
    <div>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
            <a className="navbar-brand" href="/">User-Auth</a>
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
                {loggedIn ? (
                    <ul className="navbar-nav">
                        <li className="nav-item">
                            <NavLink className="nav-link" to="/logout" onClick={logout}>Logout</NavLink>
                        </li>
                    </ul>
                    ) : (
                    <ul className="navbar-nav">
                        <li className="nav-item">
                        <NavLink className="nav-link" to="/login">Login</NavLink>
                        </li>
                        <li className="nav-item">
                        <NavLink className="nav-link" to="/signup">Signup</NavLink>
                        </li>
                    </ul>
                )}
            </div>
        </nav>
    </div>
    )
}

export default Nav
