import Nav from './Nav';

const Main = ({token}) => {

  const toggle = () => {
    if (token) {
      <h1 className='front-text'>You are Logged in!</h1>
    } else {
      <h1 className='front-text'>This is User Authorization with Node.js and React</h1>
    }
  }

  return (
    <>
    <Nav /> 
    <div className='main'>   
      {token ? (
        <h1 className='front-text'>You are Logged in!</h1>
      ) : (
        <>
          <h1 className='front-text'>This is User Authorization with Node.js and React</h1>
          <h3>Please Signup or Login</h3>
        </>
      )}
    </div>
      </>
  )
}

export default Main
