import { useState } from "react";
import Nav from "./Nav";

const Login = () => {

    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [token, setToken] = useState('')
    const [alert, setAlert] = useState(false)

    async function handleSubmit(e) {
		e.preventDefault();
		const res = await fetch('http://localhost:3001/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                username,
                password
            })
        })
        const data = await res.json()
        console.log(data)
        setToken(data.token)

        // after 5 seconds useNavigate to /main
	}


    const onAlert = () => {
        setAlert(true)
    }
    setTimeout(() => {
        setAlert(false)
        window.location.href = '/'
    }
    , 3000)

return (
    <>
    <Nav token={token} />
    <div className="login-container">
        <form onSubmit={handleSubmit} className="login-form">
            <h3>
                <strong>Member Login</strong>
            </h3>
            <div className="form-group">
                <label htmlFor="username">Username</label>
                <input type="text" className="form-control" id="username" placeholder="Enter username" value={username} onChange={(e) => setUsername(e.target.value)} />
            </div>
            <div className="form-group">
                <label htmlFor="password">Password</label>
                <input type="password" className="form-control" id="password" placeholder="Enter password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </div>
            <button className=" login-btn" onClick={onAlert}>
                Submit
            </button>
            {alert && <div className="alert alert-success alerts" role="alert">
            You have Logged in!
            </div>} 
            <div className="login-link">
                <a href="signup">Don't have a account? Sign up</a>
            </div>
        </form>
    </div>
</>
)
}

export default Login
